import React, { useState } from 'react'
import '../node_modules/bootstrap/dist/css/bootstrap.min.css'
import Menus from './Menus'
import "./index.css"

function Gallery() {
    const [items, setItems] = useState(Menus);

    const filterItem = (categItem) => {
        const updates = Menus.filter((currItem) => {
            return currItem.category === categItem;
        })
        setItems(updates)
    }

    return (
        <>
            <h1 className="mt-2 text-center main-heading text-capitalize  " key={id}>order your favourite dish</h1>
            <hr />
            <div className="menu-tabs container">
                <div className="menu-tab d-flex justify-content-around ">
                    <button className='btn btn-warning' onClick={() => filterItem("breakfast")} >BreakFast</button>
                    <button className='btn btn-warning' onClick={() => filterItem("lunch")} >Lunch</button>
                    <button className='btn btn-warning' onClick={() => filterItem("evening")} >Evening</button>
                    <button className='btn btn-warning' onClick={() => filterItem("dinner")} >Dinner</button>
                    <button className='btn btn-warning' onClick={() => setItems(Menus)} >All time</button>
                </div>
            </div>

            {/*=================== main section is start============== */}


            <div className="menu-items container-fluid mt-5" >
                <div className="row">
                    <div className="col-11 mx-auto">
                        <div className="row my-5">


                            {
                                items.map((ele) => {
                                    const {id, name, image, description, price } = ele;
                                    return (

                                        <div className="boxs" id='w'>
                                            <div className="bo" >
                                                <div className="imgbx">
                                                    <img src={image} alt="" />
                                                </div>
                                                <div className="text">
                                                    <h1>{name}</h1>
                                                    <p>Lorem ipsum dolor sit amet consectetur adipi sicing elit. Possimus voluptates.</p>
                                                    <div className="butn">
                                                        <p>Price : {price}</p>
                                                        <button className='btn btn-primary' >Order Now</button>
                                                    </div>
                                                    <h4>{description}</h4>
                                                </div>
                                            </div>
                                        </div>
                                    )
                                })
                            }

                        </div>
                    </div>
                </div>
            </div>

        </>
    )
}

export default Gallery ;
