import React, { useState } from 'react'
import '../node_modules/bootstrap/dist/css/bootstrap.min.css'
import Menus from './Menus'
import "./index.css"

function Gallery() {
    const [items, setItems] = useState(Menus);

    const filterItem = (categItem) => {
        const updates = Menus.filter((currItem) => {
            return currItem.category === categItem;
        })
        setItems(updates)
    }

    return (
        <>
            <h1 className="mt-2 text-center main-heading text-capitalize  " >order your favourite dish</h1>
            <hr />
            <div className="menu-tabs container">
                <div className="menu-tab d-flex justify-content-around ">
                    <button className='btn btn-warning' onClick={() => filterItem("breakfast")} >BreakFast</button>
                    <button className='btn btn-warning' onClick={() => filterItem("lunch")} >Lunch</button>
                    <button className='btn btn-warning' onClick={() => filterItem("evening")} >Evening</button>
                    <button className='btn btn-warning' onClick={() => filterItem("dinner")} >Dinner</button>
                    <button className='btn btn-warning' onClick={() => setItems(Menus)} >All time</button>
                </div>
            </div>

           

        </>
    )
}

export default Gallery ;
